def compute_cabin_mass(frame_spacing,sparcap_radius):
    # Initialize variables with appropriate names
    w = frame_spacing
    r = sparcap_radius
    fuselage_mass = 2 * (12539.448 + (-95489.646117) * w + (0.086875) * r + (545655.380000) * w**2 + (-0.370000) * w * r + (266341.875000) * r**2 + (-1091311.333333) * w**3 + (3.000000) * w**2 * r + (-7.500000) * w * r**2 + (31.250000) * r**3)
