    fuselage_y_cg = 0 # symetrical
    fuselage_z_cg = (0.706 + (3.057611) * w + (-1.008839) * r + (-17.286704) * w**2 + (12.535421) * w * r + (64.679687) * r**2 + (35.954800) * w**3 + (-40.520900) * w**2 * r + (36.325250) * w * r**2 + (-245.666667) * r**3)
    fuselage_Ixx = 2 * (242188.863 + (-1806443.099340) * w + (1318.291115) * r + (10329440.413138) * w**2 + (-84656.428373) * w * r + (6396781.233392) * r**2 + (-20681904.928396) * w**3 + (368687.799960) * w**2 * r + (-731726.854280) * w * r**2 + (-2847550.188522) * r**3)
    fuselage_Iyy = 2 * (153127.818 + (-1267117.146333) * w + (29579.620833) * r + (7177903.400000) * w**2 + (-662052.800000) * w * r + (2558900.000000) * r**2 + (-14403986.666667) * w**3 + (2560140.000000) * w**2 * r + (-3786750.000000) * w * r**2 + (-6242708.333334) * r**3)
    fuselage_Izz = 2 * (343412.953 + (-2659965.409840) * w + (28262.786948) * r + (15130092.513138) * w**2 + (-577424.228373) * w * r + (7104759.358392) * r**2 + (-30285284.928396) * w**3 + (2191447.799960) * w**2 * r + (-3054751.854280) * w * r**2 + (-3395258.521856) * r**3)
    return [fuselage_mass,fuselage_x_cg,fuselage_y_cg,fuselage_z_cg, fuselage_Ixx, fuselage_Iyy, fuselage_Izz]